import './App.css';
import Alert from './Components/Alert';
import Navbar from './Components/Navbar';
import About from './Components/About';
import TextForm from './Components/TextForm';
import React,{useState} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


let name = "Rishabh"
function App() {

  const [mode, setMode] = useState('light');
  const [btn, setbtn] = useState('dark');
  const [alert, setAlert] = useState(null);

  const showAlert = (message,type)=>{
    setAlert({
      msg: message,
      type: type
    })
    setTimeout(() => {
      setAlert(null);
    },2500)
  } 
  const toggleMode = ()=>{
    if(mode === 'light') {
      setMode('dark');
      setbtn('light');
      document.body.style.backgroundColor = '#0B2447';
      showAlert("Dark mode has been enabled" , "Success" );
    }
    else {
      setMode('light');
      setbtn('dark');
      document.body.style.backgroundColor = '#ffffff';
      showAlert("Light mode has been enabled" , "Success" );
    }
  } 

  return (

    <>
    {/*This is a JSX comment. We use empty brackets because JSX can only return one tag
    , also while using tags that doesn't have closing tag needs to be end with backward
    slash like this :  */}
    <Router>
      <Navbar title={name} aboutText="About" mode={mode} toggleMode={toggleMode} btn={btn}/>
      <Alert alert={alert}/>


        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <div className='container'>
          <Switch>
            <Route path="/about">
              <About />
            </Route>
            <Route path="/home">
              <TextForm heading="Enter text to Analyze" mode={mode} showAlert={showAlert} />
            </Route>
          </Switch>
        </div>
    </Router>
    </>
  );
}

export default App;
